import React, { useEffect } from 'react';
import { useRef, useState, useContext } from 'react';
import'../styles/tour-details.css';
import { useParams } from 'react-router-dom';
import { Col, Container, Form, ListGroup, Row } from 'reactstrap';
import calculateAvgRating from '../Utils/avgRating';
import avatar from "../assets/images/avatar.jpg";
import Booking from '../components/Booking/Booking';
import useFetch from './../hooks/useFetch';
import { BASE_URL } from './../Utils/config';
import Newletter from './../shared/Newsletter';

import { AuthContext } from './../context/AuthContext'

const TourDetails = () => {

    const {id} = useParams()
    const reviewMsgRef = useRef('');
    const [tourRating, setTourRating] = useState(null)
    const {user} = useContext(AuthContext)

    //later call to api
    const {data:tour, loading, error} = useFetch(`${BASE_URL}/tours/${id}`)

    const{photo, title, desc, price, address, reviews, city, distance, maxGroupSize}= tour;

    const { totalRating, avgRating} = calculateAvgRating(reviews)
    
    //format data
    const options = {day:'numeric', month:'long', year:'numeric'}
    
    // submit request to the server
    const submitHandler = async e=>{
      e.prevenyDefault()
      const reviewText = reviewMsgRef.current.value;

      try {

        if(!user || user === undefined || user === null){
          alert('Please Sign IN')
        }

        const reviewObj = {
          username:user.username,
          re
        }

        const res = await fetch(`${BASE_URL}/review/${id}`)
        
      } catch (err) {
        
      }

    }
      
    useEffect(()=>{
      window.scrollTo(0,0)
    },[tour])

    return(
      <>
      <section>
        <Container>
          {loading && <h4 className='text-center pt-5'>Loading........</h4>}
          {error && <h4 className='text-center pt-5'>{error}</h4>}
          {
            !loading && !error && <Row>
            <Col>
              <div className="tour__center">
                <img src={photo} alt="" />
                <div className="tour__info">
                  <h2>{title}</h2>
                    <div className="d-flex align-item-center gap-5">
                    <span className="tour__location d-flex align-item-center gap-1">
                    <i className="ri-map-pin-line"></i>{city}
                    </span>
                      <span className="tour__rating d-flex align-item-center gap-1">
                          <i className="ri-star-fill" style={{color:"var(--secodary-color)"}}></i>{avgRating ===0 ? null : avgRating}
                          {totalRating ===0 ? (
                              "not rated"
                          ):(
                              <span>({reviews?.length})</span>
                          )}
                      </span>
                            <span>
                              <i className='ri-map-pin-fill'></i>{address}
                            </span>
                    </div>
                    <div className="tour_extra-details">
                      <span><i className='ri-map-pin-2-line'></i>{city}</span>
                      <span><i className='ri-money-dollar-circle-line'></i>{price} /per</span>
                      <span><i className='ri-map-pin-time-line'></i>{distance} k/m</span>
                      <span><i className='ri-group-line'></i>{maxGroupSize} people</span>
                    </div>
                    <h5>Description</h5>
                    <p>{desc}</p>
                </div>
                {/* tour review section start */}
                <div className="tour__review mt-4">
                  <h4>Reviews({reviews?.length}reviews)</h4>

                  <Form onSubmit={submitHandler}>
                    <div className="d-flex align-items-center gap-3 mb-4 rating__group">
                          <span onClick={()=> setTourRating(1)}>1<i className='ri-star-s-fill'></i></span>
                          <span onClick={()=> setTourRating(2)}>2<i className='ri-star-s-fill'></i></span>
                          <span onClick={()=> setTourRating(3)}>3<i className='ri-star-s-fill'></i></span>
                          <span onClick={()=> setTourRating(4)}>4<i className='ri-star-s-fill'></i></span>
                          <span onClick={()=> setTourRating(5)}>5<i className='ri-star-s-fill'></i></span>
                    </div>
                    <div className="review__input">
                      <input type="text" ref={reviewMsgRef} placeholder='share your thoughts' required />
                      <button className='btn primary__btn text-white' type="submit">Submit</button>
                    </div>
                  </Form>
                  <ListGroup className='user__reviews'>
                    {
                      reviews?.map(review=>(
                        <div className="review__item">
                          <img src={avatar} alt="" />

                          <div className="w-100">
                            <div className="d-flex align-item-center justify-content-between">
                              <div>
                                <h5>Vijitha</h5>
                                <p>
                                  {new DataTransfer("11-06-1999").tolcaleDateString(
                                    "en-US",options
                                  )}
                                </p>
                              </div >
                                <span className='d-flex align-item-center'>
                                  5 <i className='ri-star-s-fill'></i>
                                </span>
                            </div>
                            <h6>Amezing tour</h6>
                          </div>
                        </div>
                      ))
                    }
                  </ListGroup>
                </div>
                {/* tour review section end */}
              </div>
            </Col>
            <Col lg="4">
                    <Booking tour={tour} avgRating={avgRating} />
            </Col>
          </Row>
          }
        </Container>
      </section>
    </>
    )
}

export default TourDetails;
